    parser = CssParser::Parser.new
    parser.add_block!(content)

    assets = []

    parser.each_rule_set do |set|
      set.each_declaration do |dec, val|
        if ['background-image', 'background'].include? dec
          next unless val.start_with? 'url'
          assets << un_url(val)
        end

        if dec == 'src'
          un_fontface_url(val)
        end
      end
    end

    #puts assets.inspect
  end

  private

  def un_url(arg, tag = 'lru')
    # remove url(
    arg = arg.reverse.chomp("(#{tag}").chomp('"').reverse

    # remove leading slash
    arg = arg.reverse.chomp('/').reverse if arg.start_with? '/'

    # remove )
    arg = arg.chomp(')').chomp('"')

    arg
  end

  def un_fontface_url(arg)
    arg = arg.split('url')
    arg.shift

    arg.each do |res|
      res = un_url(res, '')

      # TODO strip the other end
      puts res.inspect
    end
  end
end
